#xfce-header{background:#333 url(data:image/gif;base64,) no-repeat 3px 50%;box-shadow:0 0 8px rgba(0,0,0,0.5);font-family:'Helvetica Neue Light',HelveticaNeue-Light,'Helvetica Neue',Helvetica,Arial,sans-serif;font-size:14px;height:35px;}#xfce-header.new{background-image:none;}#xfce-header .main_width{background:url(data:image/gif;base64,) no-repeat 3px 50%;height:35px;}#xfce-header ul{list-style:none;float:right;margin:5px;padding:0;}#xfce-header ul li{float:left;margin:0; }#xfce-header a{color:#999;text-decoration:none;display:block;padding:0 8px 0 8px;border:0; line-height:25px;transition:color 0.2s linear;-moz-transition:color 0.2s linear;-webkit-transition:color 0.2s linear;-o-transition:color 0.2s linear;}#xfce-header ul li a:hover{color:#fff;text-decoration:underline;}#xfce-header .active{color:#fff;}#xfce-header-title{display:none;}#xfce-header-clear{clear:both;}#xfce-header-subtitle{display:none;}body,h1,h2,h3,h4,ol,ul,li{margin:0;padding:0;font-weight:normal;font-style:normal;}body{font-family:"Bitstream Vera Sans","Lucida Grande","Lucida Sans Unicode",Helvetica,Arial,Verdana,sans-serif;font-size:80%;line-height:1.5em;text-align:left;background-color:#eee;color:#000;}h1,h2,h3,h4,dl dt{ color:#333; }h1{ font-size:180%; font-weight:normal; margin:0.5em 0 1em 0; }h2,h3,h4{ font-weight:bold; margin:1em 0; }h2{ font-size:125%; }h3{ font-size:100%; }h4{ font-size:90%; }h2 em{font-weight:normal;}a,a:link,a:visited{color:#06c;text-decoration:none;outline:none;}a:hover{color:#f00;text-decoration:underline;}a span{color:#333;}a:hover span{color:#f00;}hr{border:none;border-top:1px solid rgba( 0,0,0,0.2 );margin:2em 0;}ul{list-style-type:square;}dl dt{font-weight:bold;}dl dd{margin:0 0 1em 3em;}table{width:100%;font-size:90%;border-collapse:collapse;border:1px solid rgba( 0,0,0,0.2 );margin:1em 0;}table tr{border-bottom:1px solid rgba( 0,0,0,0.2 );}table thead th{background-color:rgba( 0,0,0,0.05 );}table th,table td{padding:0.2em 0.5em;vertical-align:top;}table td{border-right:1px solid transparent;}table td:last-child,table th,table tr:hover td{border-right:1px solid rgba( 0,0,0,0.2 );}input[type="submit"],button,select{border-radius:0.2em;border:1px solid rgba( 0,0,0,0.2 );background:#f7f7f7 linear-gradient( to bottom,rgba( 255,255,255,0.5 ),rgba( 0,0,0,0.02 ) 1px,rgba( 0,0,0,0.05 ) ) repeat-x;color:#000;padding:0.2em 0.5em;outline:none !important;transition:150ms all;}select{-moz-appearance:none;-webkit-appearance:none;appearance:none;}input[type="submit"]:hover,button:hover,select:hover{background-color:#fff;color:#06c;text-decoration:none;}input,input[type="text"],textarea{background-color:#f7f7f7;color:#000;border-radius:0.2em;border:1px solid rgba( 0,0,0,0.2 );padding:0.2em 0.5em;}input[type="file"]{border:none;}textarea{font-family:"Bitstream Vera Sans","Lucida Grande","Lucida Sans Unicode",Helvetica,Arial,Verdana,sans-serif;font-size:100%;}input[type="checkbox"]{opacity:0.75;transition:150ms all;position:relative;vertical-align:middle;}input[type="checkbox"]:checked{opacity:1;}.main_width,#main{width:95%;min-width:830px;max-width:1400px;margin:0 auto;padding:0 0.5em;}#content{display:-webkit-box;display:-ms-flexbox;display:flex;background-color:#fff;box-shadow:0 0 3px rgba(0,0,0,.25);border-radius:4px;}#article{-webkit-box-flex:70;-ms-flex:70;flex:70;padding:2em;border-right:1px solid #ccc;line-height:1.7em;}#article ul,#article ol{list-style-position:outside;margin-left:3em;}#related{-webkit-box-flex:30;-ms-flex:30;flex:30;max-width:230px;padding:1em 1.5em;}#related:empty{display:none;}#related h2{margin:1em 0 0.5em 0;font-weight:normal;}#related ul{margin:0 0 0 2em;}#related ul li{margin-bottom:0.5em;}#related ul li a{display:block;}#related-jump{display:none;}#footer{color:#999;text-align:center;}.group:after{content:".";display:block;height:0;clear:both;visibility:hidden;}.hidden{display:none;}.mungify{unicode-bidi:bidi-override;direction:rtl;}a span{color:inherit;}a.wikilink2{color:red;}#header .breadcrumbs{float:left;}#header .tools{float:right;margin-top:-0.4em;}.mobileTools{display:none;}@media screen and (max-width:850px){#header .tools div,#header .tools select,#header .tools form,.mobileTools{display:inline;}}.dokuwiki div.page{box-shadow:none;border:none;border-top-right-radius:0;border-bottom-right-radius:0;margin-bottom:0;}.dokuwiki #related ul{margin-left:1em;}#dw__toc{margin:0;width:100%;border:none;margin-bottom:1em;}#dw__toc h3{font-weight:normal;font-size:125%;letter-spacing:inherit;padding:0;}#dw__toc h3 strong{display:inline-block;width:1.3em;margin-right:0.5em;text-align:center;background-color:rgba(0,0,0,0.1);}#dw__toc > div{font-size:100%;}#dw__toc ul{margin-left:1em;}#dw__toc ul li{list-style-image:none;list-style-type:square;}#dw__toc li ul{margin-left:2em;margin-top:0.5em;}#dw__toc a{color:#333;}#header,#footer{padding:0.4em 0;}#header{padding-top:0.8em;}#footer .bars{display:flex;margin-bottom:0.4em;}#footer .bars div{flex:1;}#footer .bar-left{text-align:left;}#footer .bar-right{text-align:right;}#footer .bar-left form,#footer .bar-right form{display:inline;}div.plugin_translation select{background-color:#f7f7f7 !important;padding:0.2em 0.5em !important;border:1px solid rgba( 0,0,0,0.2 ) !important;border-radius:0.2em !important;box-shadow:none !important;color:#000 !important;}pre.code{background-color:rgba( 0,0,0,0.04 );border:1px solid rgba( 0,0,0,0.1 );border-radius:0.3em;padding:0.3em 0.8em;}@media screen and (max-width:850px){h1{margin-top:0.3em;font-size:160%;text-align:left;}.main_width,#main{min-width:300px;}#content{display:block;}#article{border:none;padding:1.5em;}#related{background-color:#fafafa;border-top:1px solid #eee;max-width:100%;padding-bottom:1.5em;border-bottom-right-radius:4px;border-bottom-left-radius:4px;}#related-jump{float:right;display:block;background-color:#eee;margin:-1em -1em 0 0;border-top-right-radius:4px;border-bottom-left-radius:4px;}#related-jump a{padding:0.3em 0.6em;display:block;}}